SELECT COUNT(*) as inserted_events FROM schedule_events WHERE date >= '2025-09-01' AND date <= '2025-09-30';
SELECT 
  s.title as scenario_title,
  s.author,
  COUNT(se.id) as event_count
FROM schedule_events se
JOIN scenarios s ON se.scenario_id = s.id
WHERE se.date >= '2025-09-01' AND se.date <= '2025-09-30'
GROUP BY s.title, s.author
ORDER BY s.author, s.title;
