-- 実行確認
SELECT COUNT(*) as inserted_events FROM schedule_events;
SELECT category, COUNT(*) as event_count FROM schedule_events GROUP BY category ORDER BY category;
SELECT 
  s.author,
  COUNT(se.id) as total_events,
  COUNT(CASE WHEN se.category = 'gmtest' THEN 1 END) as gm_test_events,
  COUNT(CASE WHEN se.category = 'open' THEN 1 END) as normal_events
FROM schedule_events se
JOIN scenarios s ON se.scenario_id = s.id
GROUP BY s.author
ORDER BY s.author;
